<?php

declare(strict_types=1);

namespace EngineeredTests\Unit;

use Engineered\AbstractProduct\Domain\AbstractProduct;
use Engineered\HttpClient\HttpClientFacadeInterface;
use PHPUnit\Framework\TestCase;

class AbstractProductTest extends TestCase
{
    public function test_the_abstract_product_is_returned(): void
    {
        $httpClientFacade = $this->createMock(HttpClientFacadeInterface::class);
        $httpClientFacade->method('get')->willReturn($this->getReturn());
        $product = new AbstractProduct($httpClientFacade);

        $response = $product->get('208');

        self::assertEquals('208', $response['data']['id']);

        $this->assertIsArray($response);
    }

    public function test_the_related_abstract_products_are_returned(): void
    {
        $httpClientFacade = $this->createMock(HttpClientFacadeInterface::class);
        $httpClientFacade->method('get')->willReturn($this->httpClientResponse());
        $product = new AbstractProduct($httpClientFacade);

        $response = $product->getRelated('202');

        self::assertEquals('016', $response['data'][0]['id']);
        self::assertEquals('020', $response['data'][4]['id']);
        self::assertEquals('024', $response['data'][8]['id']);

        $this->assertIsArray($response);
    }

    public function test_the_search_results_are_returned(): void
    {
        $httpClientFacade = $this->createMock(HttpClientFacadeInterface::class);
        $httpClientFacade->method('get')->willReturn($this->httpClientSearchResponse());
        $product = new AbstractProduct($httpClientFacade);

        $response = $product->search('canon');

        self::assertEquals('037', $response['data'][0]['attributes']['abstractProducts'][4]['abstractSku']);
        self::assertEquals('16999', $response['data'][0]['attributes']['abstractProducts'][4]['price']);
        self::assertEquals('Canon PowerShot SX710', $response['data'][0]['attributes']['abstractProducts'][4]['abstractName']);

        $this->assertIsArray($response);
    }

    public function test_the_category_results_are_returned(): void
    {
        $httpClientFacade = $this->createMock(HttpClientFacadeInterface::class);
        $httpClientFacade->method('get')->willReturn($this->httpClientCategoryResponse());
        $product = new AbstractProduct($httpClientFacade);

        $response = $product->getAbstractProductsByCategoryId(1);

        self::assertEquals('009', $response['data'][0]['attributes']['abstractProducts'][0]['abstractSku']);
        self::assertEquals('10580', $response['data'][0]['attributes']['abstractProducts'][1]['prices'][0]['grossAmount']);

        $this->assertIsArray($response);
    }

    public function test_that_single_attributes_are_returned(): void
    {
        $httpClientFacade = $this->createMock(HttpClientFacadeInterface::class);
        $httpClientFacade->method('get')->willReturn($this->getReturn());
        $product = new AbstractProduct($httpClientFacade);

        $response = $product->getReviewCount('208');
        self::assertEquals(0, $response);

        $response = $product->getName('208');
        self::assertEquals('Toshiba CAMILEO P20', $response);

        $response = $product->getDescription('208');
        self::assertEquals(
            'Experience it The Toshiba Full HD Camileo S40 is eye catching in more ways than one. Its attractive, super-slim design allows you to take it everywhere, so you can capture everything that pleases you. Featuring a choice of colour accents, it also doubles as an elegant 16 megapixel digital still camera. Just pull it out of your pocket, and shoot stunning 1920x1080p Full HD video with up to 5x digital zoom. 10x digital zoom is available for HD and VGA resolution. Electronic Image Stabilisation (EIS) is built in for great results -- and sharing to social networks is a breeze thanks to Camileo Uploader software.',
            $response
        );

        $response = $product->getMetaTitle('208');
        self::assertEquals('Toshiba CAMILEO P20', $response);

        $response = $product->getMetaKeywords('208');
        self::assertEquals('Toshiba,Smart Electronics', $response);

        $response = $product->getMetaDescription('208');
        self::assertEquals(
            'Experience it The Toshiba Full HD Camileo S40 is eye catching in more ways than one. Its attractive, super-slim design allows you to take it everywhere, so',
            $response
        );
    }

